      updateMedicalCertificateMutation.mutateAsync({
        id: currentMedCertId,
      }),
      {
        loading: "Updating...",
        id: "generalCertupdate", // <-- toast id
        success: {
          message: "Saved",
        },
        error: (error) => {
          /* problem üëá this will pollute future toast context */
          return {
            duration: 3000,
            message: error.message || "Failed to update leave category",
            action: {
              label: "Go back",
              onClick: goBack,
            },
          }
        },
        position: "top-right",
        toasterId: "medcertSuccess", // <-- toaster id (see above)
      },
    )
